function postCodes(knwlInstance) {
		
        var postcodeRegex = /[A-Z]{1,2}(([0-9]{1,2})|([0-9][A-Z]))\s[0-9][A-Z]{1,2}/g;

    	this.languages = { //supported languages
			english: true	
		};
    
        function findMatch(regex, text, array) {
            var matchcases = text.match(regex);

            for (var matchcase in matchcases) {
                if(array.includes(matchcases[matchcase]) == false)
                    array.push(matchcases[matchcase]);
            }
        };

		this.calls = function() {
			
			var words = knwlInstance.words.get('linkWordsCasesensitive'); //get the String as an array of words
            linkedWords = words.join(' ');
			var postcodes = [];
			
            findMatch(postcodeRegex, linkedWords, postcodes);
			
		    return postcodes;
		};
	}
	
	module.exports = postCodes;